            {
                foreach (var dependency in GetTypeDependencies(type))
                {
                    if (forbiddenDependencies.Any(ns => 
                        dependency.Namespace?.StartsWith(ns) == true ||
                        dependency.Assembly.GetName().Name?.StartsWith(ns.Split('.')[0]) == true))
