        protected virtual void Dispose(bool disposing)
        {
            if (!_disposed && disposing)
            {
                foreach (var entitySet in _entitySets.Values)
                {
                    if (entitySet is IDisposable disposable)
                    {
                        disposable.Dispose();
                    }
                }
                _entitySets.Clear();
                _entityModels.Clear();
                _disposed = true;
            }
